    '/\/[^\/\n]+\/[gimuy]*/',
    // Chemins de fichiers
    '/\.(css|js|php|twig|html|xml|json|yaml|yml|md|txt|log|pdf|jpg|jpeg|png|gif|svg|webp|mp4|avi|mov|pdf|doc|docx|xls|xlsx|ppt|pptx|zip|rar|tar|gz)(\?|$|[\'\")\]\s])/i',
];

function shouldIgnoreLine($line, $ignorePatterns) {
    foreach ($ignorePatterns as $pattern) {
        if (preg_match($pattern, $line)) {
            return true;
        }
    }
    return false;
}

$totalIssues = 0;
$fileExtensions = ['.php', '.twig'];

// Parcourir tous les fichiers PHP et Twig
$iterator = new RecursiveIteratorIterator(
    new RecursiveDirectoryIterator(__DIR__),
    RecursiveIteratorIterator::LEAVES_ONLY
);

foreach ($iterator as $file) {
    if ($file->isFile()) {
        $extension = '.' . strtolower($file->getExtension());
        if (in_array($extension, $fileExtensions)) {
            $filepath = $file->getPathname();
            
            // Ignorer certains dossiers
            if (strpos($filepath, '/vendor/') !== false ||
                strpos($filepath, '/var/cache/') !== false ||
                strpos($filepath, '/node_modules/') !== false ||
                strpos($filepath, '/.git/') !== false) {
                continue;
            }
            
            $content = file_get_contents($filepath);
            $lines = explode("\n", $content);
            $issues = [];
            
            foreach ($lines as $lineNumber => $line) {
                $lineNumber++; // 1-indexed
                
                // Ignorer certains patterns
                if (shouldIgnoreLine($line, $ignorePatterns)) {
                    continue;
                }
                
                // Vérifier chaque pattern
                foreach ($patterns as $pattern) {
                    if (preg_match($pattern, $line, $matches)) {
                        $issues[] = [
                            'line' => $lineNumber,
                            'match' => $matches[0],
                            'context' => trim($line)
                        ];
                        break; // Un seul problème par ligne
                    }
                }
            }
            
            if (!empty($issues)) {
                $totalIssues += count($issues);
                $relativePath = str_replace(__DIR__ . '/', '', $filepath);
                echo "📁 {$relativePath}\n";
                echo str_repeat('-', strlen($relativePath) + 2) . "\n";
                
                foreach ($issues as $issue) {
                    echo "  Ligne {$issue['line']}: {$issue['match']}\n";
                    echo "    Contexte: {$issue['context']}\n\n";
                }
            }
        }
    }
}

echo "\n========================================\n";
echo "TOTAL: {$totalIssues} instances de texte hardcodé détectées\n";
echo "========================================\n";